import logo from './logo.svg';
import './App.css';
import Quotes from './Quotes'
import React, { useState } from 'react';

function App() {
  const mainCategories = ["Educational", "Inspirational", "Love"];
  const subCategories = {
    Educational: ["Science", "Literature", "Art"],
    Inspirational: ["Exercise", "Goals", "Money"],
    Love: ["Family", "Friends", "Animals"],
  };

  const [selectedCategory, setSelectedCategory] = useState(null);

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div>
      <h1>Category Selection</h1>
      {mainCategories.map((category) => (
        <button
          key={category}
          onClick={() => handleCategoryClick(category)}
          style={{ marginRight: '10px' }}
        >
          {category}
        </button>
      ))}

      {selectedCategory && (
        <div>
          <h2>Subcategories for {selectedCategory}:</h2>
          {subCategories[selectedCategory].map((subCategory) => (
            <button key={subCategory}>{subCategory}</button>
          ))}
          {/* <h2>Quotes for {selectedCategory}:</h2> */}
          {Quotes[selectedCategory][subCategories[selectedCategory]].map((quoteObj, index) => (
            <div key={index}>
              <p>{quoteObj.quote}</p>
              <p>- {quoteObj.author}</p>
            </div>
          ))}
        </div>


      )}
    </div>
  );

  // const level1 = ["Educational", "Inspirational", "Love"];

  // return (
  //   <div className="App">
  //     <div>something</div>
  //     <div className='container'>
  //       {level1.map((category, index) => (
  //         <button key={index} className="button">
  //           {category}
  //         </button>
  //       ))}
  //     </div>
  //   </div>
  // );
}

export default App;
